"use strict";window.addEventListener("DOMContentLoaded",()=>{
//!Меню бургер
const e=document.querySelector(".header__list"),t=document.querySelectorAll(".header__item"),r=document.querySelector(".header__burger");r.addEventListener("click",()=>{r.classList.toggle("header__burger_active"),e.classList.toggle("header__list_active")}),t.forEach(t=>{t.addEventListener("click",()=>{r.classList.toggle("header__burger_active"),e.classList.toggle("header__list_active")})});
//!Плавный скрол до элементов с якорем
const c=document.querySelectorAll('a[href *="#"]');for(let e of c)e.addEventListener("click",(function(t){t.preventDefault();const r=e.getAttribute("href");document.querySelector(""+r).scrollIntoView({behavior:"smooth",block:"start"})}))});